import { GET_ITEMS, ADD_ITEM, DELETE_ITEM } from './types'

// sometimes seen as function getItems() {}
export const getItems = () => {
  return {
    type: GET_ITEMS
  }
}

export const deleteItems = (id) => {
  return {
    type: DELETE_ITEM,
    payload: id
  }
}
