import React, { Component } from 'react'
import { Container, ListGroup, ListGroupItem, Button } from 'reactstrap'
import { CSSTransition, TransitionGroup } from 'react-transition-group'
import uuid from 'uuid'
import { connect } from 'react-redux'
import { getItems } from '../actions/itemActions'
import PropTypes from 'prop-types'

class ShoppingList extends Component {

  componentDidMount() {
    this.props.getItems()
  }

  /*state = {
    items: [
      { id: uuid(), name: 'water' },
      { id: uuid(), name: 'cucumber' },
      { id: uuid(), name: 'tomato' }
    ]
  }*/

  render() {

    // this.state.items // using destructuring instead
    // const { items } = this.state // pull items from component state w was up
    // before mapping to props

    // item represent the entire state object and items the array
    // this.props.item.items

    const { items } = this.props.item

    return (

      <Container>

        <Button
          color="dark"
          style={{marginBottom: '2rem'}}
          onClick={() => {
            const name = prompt('Enter item')
            if(name) {
              this.setState(state => ({
                items: [...state.items, { id: uuid(), name }]
              }))
            }
          }}
        >Add Item</Button>

        <ListGroup>
          <TransitionGroup className="shopping-list">

            {items.map(({ id, name }) => (
              <CSSTransition key={id} timeout={500} classNames="fade">
                <ListGroupItem>

                  <Button
                    className="remove-btn"
                    color="danger"
                    size="sm"
                    onClick={() => {
                      this.setState(state => ({
                        items: state.items.filter(item => item.id !== id)
                      }))
                    }}
                  >
                  &times;
                  </Button>

                  {name}

                </ListGroupItem>

              </CSSTransition>
            ))}

          </TransitionGroup>
        </ListGroup>

      </Container>

    )
  }

}

ShoppingList.propTypes = {
  getItems: PropTypes.func.isRequired,
  item: PropTypes.object.isRequired
}

const mapStateToProps = (state) => ({
  // this item refers to reducers/index.js
  item: state.item
})

export default connect(mapStateToProps, { getItems })(ShoppingList)
